JIRA.Dropdowns.bindHeaderDropdown2=function(){AJS.$(".aui-dropdown2-trigger").each(function(){var a=AJS.$(this);var c=AJS.$("#"+a.attr("aria-owns"));var b=c.data("aui-dropdown2-ajax-key");if(b){c.bind("aui-dropdown2-show",function(){c.empty();c.addClass("aui-dropdown2-loading");JIRA.SmartAjax.makeRequest({url:contextPath+"/rest/api/1.0/menus/"+b,dataType:"json",cache:false,success:function(d){c.removeClass("aui-dropdown2-loading");c.html(JIRA.FRAGMENTS.dropdown2Fragment(d));c.find("a:not(.disabled)").filter(":first").addClass("active")}})})}})};AJS.$(function(){JIRA.Dropdowns.bindHeaderDropdown2()});JIRA.FRAGMENTS.dropdown2Fragment=function(b){var a=AJS.$("<div />");AJS.$(b.sections).each(function(){var f=AJS.$('<div class="aui-dropdown2-section" />');var e=AJS.$("<ul class='aui-list-truncate' />");var c;var d;if(this.id){e.attr("id",this.id)}if(this.style){e.addClass(this.style)}if(this.items&&this.items.length!=0){if(this.label){f.append(AJS.$("<strong/>").text(this.label))}AJS.$(this.items).each(function(){c=AJS.$("<li />");if(this.id){c.attr("id",this.id)}if(this.style){c.addClass(this.style)}d=AJS.$("<a />").attr("href",this.url);if(this.id){d.attr("id",this.id+"_lnk")}if(this.title){d.attr("title",this.title)}if(this.iconUrl){d.addClass("aui-icon-container").css("background-image","url('"+this.iconUrl+"')")}if(this.label){d.text(this.label)}c.append(d);e.append(c)});f.append(e);a.append(f)}});return a.children()};